query:
fragment ProductAttribute on ProductAttribute {
  name
  attribute
  availability
  __typename
}

fragment JourneyOptionLeg on JourneyOptionLeg {
  id
  trainNumber
  trainType
  type
  commercialLineIdentifier
  departureStation
  arrivalStation
  productAttributes {
    ...ProductAttribute
    __typename
  }
  __typename
}

fragment OptionAvailability on OptionAvailability {
  seatAvailability
  accessibleSeatAvailability
  petSeatAvailability
  cabinAvailability
  petCabinAvailability
  accessibleCabinAvailability
  __typename
}

fragment JourneyOptionPassenger on JourneyOptionPassenger {
  type
  offers {
    legId
    product
    price
    discountCategory
    __typename
  }
  __typename
}

fragment JourneyOption on JourneyOption {
  id
  departureTime
  departureStation
  arrivalStation
  arrivalTime
  legs {
    ...JourneyOptionLeg
    __typename
  }
  totalPrice
  discount {
    discountPercentage
    ids
    __typename
  }
  error
  passengers {
    ...JourneyOptionPassenger
    __typename
  }
  availability {
    ...OptionAvailability
    __typename
  }
  __typename
}

query searchJourney($departureStation: String!, $arrivalStation: String!, $departureDateTime: DateTime!, $passengers: [PassengerInput!]!, $filters: [ConnectionFilter]! = [], $placeTypes: [PlaceType!]!, $bundleId: String, $companyId: String) {
  searchJourney(
    departureStation: $departureStation
    arrivalStation: $arrivalStation
    departureDateTime: $departureDateTime
    passengers: $passengers
    filters: $filters
    placeTypes: $placeTypes
    bundleId: $bundleId
    companyId: $companyId
  ) {
    ...JourneyOption
    __typename
  }
}
